import os
import platform
import netifaces as ni
import subprocess


class NCManage:
    def __init__(self):
        self.nic_list = {}
        self.get_nic()

    @staticmethod
    # 将MAC地址的后32位转换为IP地址 #####################################
    def mac_ip4(mac_addr):
        # 验证MAC地址格式
        if not mac_addr:
            raise ValueError("MAC地址不能为空")

        mac_part = mac_addr.split(":")
        if len(mac_part) < 4:
            raise ValueError(f"MAC地址格式不正确: {mac_addr}")

        ip4_part = mac_part[-4:]  # 取MAC地址的后4个部分

        # 验证每个部分都不为空且都是有效的十六进制
        for part in ip4_part:
            if not part.strip():
                raise ValueError(f"MAC地址包含空部分: {mac_addr}")
            try:
                int(part, 16)  # 验证是否为有效的十六进制
            except ValueError:
                raise ValueError(f"MAC地址包含无效部分: {part}")

        ip4_addr = ".".join(str(int(part, 16)) for part in ip4_part)
        return ip4_addr

    # 获取所有网络接口 ##################################################
    def get_nic(self):
        self.nic_list = {}
        for nic_data in ni.interfaces():
            print(nic_data)
            try:
                print(ni.ifaddresses(nic_data))
                mac = ni.ifaddresses(nic_data)[ni.AF_LINK][0]['addr']
                gateway = ni.ifaddresses(nic_data).get(ni.AF_INET, (None,))[0]
                if mac != '00:00:00:00:00:00':  # 排除无效MAC地址
                    self.nic_list[nic_data.lower()] = mac
            except KeyError:
                continue

    def get_default_gateway(self):
        gateways = ni.gateways()
        default_gateway = gateways.get('default', {}).get(ni.AF_INET, (None,))[0]
        return default_gateway

    def configure_interfaces(self):
        """
        配置所有网络接口
        """
        default_gateway = self.get_default_gateway()
        print(f"Using default gateway: {default_gateway}")
        for interface, mac in self.nic_list.items():
            try:
                ip_address = self.mac_ip4(mac)
                print(f"Interface {interface} has MAC {mac} and IP {ip_address}")
            except ValueError as e:
                print(f"Error configuring interface {interface}: {e}")
                continue

    def start_service(self):
        """
        启动服务
        """
        print("Starting Network Configurator Service...")
        self.configure_interfaces()
        print("Network configuration completed.")


if __name__ == "__main__":
    service = NCManage()
    service.start_service()

